puzz = [list(i) for i in puzz.split("\n")]

numGalaxies = 0
numRows = len(puzz)
numCols = len(puzz[0])
locations = {}
iterator = 1

for j in range(len(puzz)):
	for i in range(numCols):
		if puzz[j][i] == "#":
			numGalaxies += 1
			puzz[j][i] = iterator
			locations[iterator] = (j, i)
			iterator += 1

emptyRows = set()
emptyCols = set()

for i in range(numRows):
	if len(set(puzz[i])) == 1: emptyRows.add(i)

for i in range(numCols):
	c = set()
	for j in range(numRows):
		c.add(puzz[j][i])
	if len(c) == 1: emptyCols.add(i)

pairs = []
for i in range(1, iterator):
	for x in range(i + 1, iterator):
		pairs.append((i, x))

total = 0
for pair in pairs:
	locationOne = locations[pair[0]]
	locationTwo = locations[pair[1]]
	rowRange = range(min(locationOne[0], locationTwo[0]), max(locationOne[0], locationTwo[0]))
	colRange = range(min(locationOne[1], locationTwo[1]), max(locationOne[1], locationTwo[1]))
	tot = abs(locationOne[0] - locationTwo[0]) + abs(locationTwo[1] - locationOne[1])
	for e in emptyRows: 
		if e in rowRange: tot += 999_999
	for e in emptyCols:
		if e in colRange: tot += 999_999
	total += tot

print(total)